#include<stdio.h>
int main()
{
    int a=10,b=20;
    printf("the value of a : %d\n",a);
    printf("the value of b : %d\n",b);
    printf("the value of a and b : %d\n",a==b);
    printf("the addition of a and b : %d\n",a+=b);
    printf("the substrction of a and b : %d\n",a-=b);
    printf("the multiplication of a and b : %d\n",a*=b);
    printf("the division of a and b : %d\n",a/=b);
    printf("the remainder of a and b : %d\n",a%=b);
   
    return 0;  
    
      
}