#include<stdio.h>
int main()
{
    int a,b;
    printf("the value of a and b :%d\n",a==b);
    printf("the value of a and b :%d\n",a+=b);
    printf("the value of a and b :%d\n",a-=b);
    printf("the value of a and b :%d\n",a*=b);
    printf("the value of a and b :%d\n",a/=b);
    printf("the value of a and b :%d\n",a%=b);
    return 0;  
    
      
}